import "./register.scss"
import { Link, useNavigate } from "react-router-dom"
import { useState } from "react"
import axios from "axios"

const Register = () => {
  const navigate = useNavigate();

  const [inputs, setInputs] = useState({
    username: "",
    email: "",   // беше "emil", поправих на email
    password: "",
    name: "",
  });

  const [err, setErr] = useState(null);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8800/api/auth/register", inputs);
      navigate("/login"); // ако регистрацията е успешна -> към login
    } catch (err) {
      setErr(err.response?.data || err.message);
    }
  };

  return (
    <div className="register">
      <div className="card">
        <div className="left">
          <h1>TravelShare</h1>
          <p>
            This social media was created by the person who likes traveling.
            If you want to share your experience, good places in different countries/towns,
            or you look for these places — this social media is for you.
            Please, be nice with everyone on this website. We want to create a friendly 
            and comfortable environment for everyone.
          </p>
          <span>Do you have an account?</span>
          <Link to="/login">
            <button>Login</button>
          </Link>
        </div>
        <div className="right">
          <h1>Register</h1>
          <form>
            <input 
              type="text" 
              placeholder="Username" 
              name="username" 
              onChange={handleChange} 
            />
            <input 
              type="email" 
              placeholder="Email" 
              name="email" 
              onChange={handleChange} 
            />
            <input 
              type="password" 
              placeholder="Password" 
              name="password" 
              onChange={handleChange} 
            />
            <input 
              type="text" 
              placeholder="Name" 
              name="name" 
              onChange={handleChange} 
            />
            {err && <span style={{ color: "red" }}>{err.message}</span>}
            <button onClick={handleClick}>Register</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
